import React, { useState } from "react";
import "./App.css";
import products from "./items";
// components
import ProductList from "./components/ProductList";
//data

// ItemsDetail

import ItemsDetail from "./components/ItemsDetail";
//styles
import {
  Title,
  Description,
  ShopImage,
  GlobalStyle,
  ThemeButton,
} from "./styles";
import { ThemeProvider } from "styled-components";

const theme = {
  light: {
    maincolor: "black ",
    backgroundColor: "white",
    green: "black",
  },

  dark: {
    maincolor: "white ",
    backgroundColor: "black",
    green: "white",
    red: "red",
  },
};

function App() {
  const [currentTheme, setCurrentTheme] = useState("light");

  const [visible, umVisible] = useState(false);
  const [product, umproduct] = useState(products);

  const selectVisible = (prodectid) => {
    const selectVisible = products.find((product) => products.id === prodectid);
    umproduct(selectVisible);
    umVisible(!visible);
  };

  const handleToggle = () =>
    setCurrentTheme(currentTheme === "light" ? "dark" : "light");
  const buttonText = currentTheme === "light" ? "Dark Mode" : "Light Mode";

  return (
    <ThemeProvider theme={currentTheme === "light" ? theme.light : theme.dark}>
      <GlobalStyle />
      <ThemeButton onClick={handleToggle}> {buttonText}</ThemeButton>

      <ShopImage
        src="data:image/jpeg;base64,"
        alt="logo"
      ></ShopImage>
      <Title>Welone to our Shop</Title>
      <Description> Every thing under one roof</Description>
      {visible ? (
        <ItemsDetail product={product} />
      ) : (
        <ProductList selectVisible={selectVisible} />
      )}
    </ThemeProvider>
  );
}

export default App;
