import React, { useState } from "react";

//styles

//items list
import BoxProduct from "./BoxProduct";
import { ListWrapper } from "../styles";
import SearchBar from "./SearchBar";




const ProductList = (props) => {

  const [query, setQuery] = useState("");

  const filteredproduct = props.products.filter((product) => product.name.includes(query));

  console.log("ProductList->filteredproduct", filteredproduct);

  const itemsList = props.products.map((product) => (
    <BoxProduct
      product={product}
      deleteItem={props.deleteItem}
      selectVisible={props.selectVisible}
      key={product.id}
    />
  ));

  return (
    <>
      <SearchBar setQuery={setQuery} />
      <ListWrapper>{itemsList}</ListWrapper>;
    </>);
};

export default ProductList;
