import React from "react";
import products from "../items";

const ItemsDetail = (props) => {
  const product = props.products;

  return (
    <div>
      <h1>{products.name}</h1>,
      <img src={products.image} alt={products.name} />
      <p>{products.description}</p>
      <p>{products.price}</p>
    </div>
  );
};

export default ItemsDetail;
