import React from "react";
import products from "../items";
import { DetailWrapper } from "../styles";
const ItemsDetail = () => {
  const product = products[0];

  return (
    <div>
      <h1>{product.name}</h1>,
      <img src={product.image} alt={products.name} />
      <p>{product.description}</p>
      <p>{product.price}</p>
    </div>
  );
};

export default ItemsDetail;
